import React from 'react';
import './App.css';

const initialProductList = [
  { id: 1, name: 'produit 1', price: 50, quantity: 0, total: 0 },
  { id: 2, name: 'produit 2', price: 75, quantity: 0, total: 0 },
  { id: 3, name: 'produit 3', price: 20, quantity: 0, total: 0 }
];

function App () {
  const [products, setProducts] = React.useState(initialProductList)

  function handleQuantity(event, product) {
    var newQuantity = parseInt(event.target.value)
    var newPrice    = newQuantity * product.price || 0

    var newProduct = products.map((oldProduct) => {
      if (oldProduct.id === product.id) {
        return {...product, quantity: newQuantity, total: newPrice}
      } else {
        return {...oldProduct}
      }
    })

    setProducts(newProduct)
  }

  function total() {
    return <p>Montant de la commande : {}</p>
  }

  return (
    <div className='App'>
      <h1>Ma commande</h1>
      <table>
        <thead>
          <tr>
            <th>Produit</th>
            <th>Prix unitaire</th>
            <th>Quantit√©</th>
            <th>Prix total</th>
          </tr>
        </thead>

        <tbody>
          {products.map(product => (
            <tr key={product.id}>
              <td>{product.name}</td>
              <td>{product.price}</td>
              <td>
                <input
                  value={product.quantity}
                  type="number"
                  min="0"
                  onChange={(event) => handleQuantity(event, product)} />
              </td>
              <td>{product.total}</td>
            </tr>
          ))}
        </tbody>
      </table>


    </div>
  );
}

export default App;
