import React from 'react';
import './App.css';

const initialProductList = [
  { id: 1, name: 'produit 1', price: 50, quantity: 1 },
  { id: 2, name: 'produit 2', price: 75, quantity: 2 },
  { id: 3, name: 'produit 3', price: 20, quantity: 5 }
];

function App () {
  const totalPrice                 = React.useRef()
  const [products, setProducts] = React.useState(initialProductList)

  function handleTotal(event, product) {
    var newQuantity = parseInt(event.target.value)
    var newPrice    = newQuantity * product.price

    debugger

    var newProduct = products.map((oldProduct) => {
      if (oldProduct.id === product.id) {
        return {
          id: 1,
          name: 'produit 1',
          price: newPrice,
          quantity: newQuantity
        }
      } else {
        return {...oldProduct}
      }
    })

    setProducts(newProduct)
  }

  return (
    <div className='App'>
      <h1>Ma commande</h1>
      <table>
        <thead>
          <tr>
            <th>Produit</th>
            <th>Prix unitaire</th>
            <th>Quantit√©</th>
            <th>Prix total</th>
          </tr>
        </thead>

        <tbody>
          {products.map(product => (
            <tr key={product.id}>
              <td>{product.price}</td>
              <td>{product.name}</td>
              <td><input value={product.quantity} type="number" onChange={(event) => handleTotal(event, product)} /></td>
              <td ref={totalPrice}></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
